import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import AxiosInstance from "../Axios";
import {Button, Modal, Fade, Backdrop, makeStyles, TextField,
 Grid,  Typography
    , Container} from "@material-ui/core";



const useStyles = makeStyles((theme) => ({
    modal: {
        display: 'flex',
        alignItems: 'top',
        justifyContent: 'center',
    },
    paper: {
        borderRadius: '40px',
        display: 'flex',
        backgroundColor: 'white',
        margin: theme.spacing(6,5,13,5),
        padding: theme.spacing(6),
    },
  }));


function Registration(prop){
    const classes  = useStyles();
    const[open, setOpen] = useState(false);
    const [account, setAccount] = useState({username: '', email: '', password: ''});
    let history = useHistory();
    function handleChange(e){
        const {name, value} = e.target;
        setAccount(prevState => ({
            ...prevState,
            [name]: value
         }));
    }


    function handleSubmit(e){
        e.preventDefault();
        AxiosInstance
            .post("/api/register/", account)
            .then(res =>{
                history.push("/login");
                console.log(res);
            })
            .catch((error) => {
                console.log(error.response);
            });
    }

    function handleClick(){
        setOpen(!open);
    }

    return(
        <div>
            <Button onClick = {handleClick}>{prop.text}</Button>
            <Modal
                open = {open}
                onClose = {handleClick}
                aria-labelledby="simple-modal-title"
                aria-describedby="simple-modal-description"
                className = {classes.modal}
                BackdropComponent = {Backdrop}
                BackdropProps = {{
                    timeout: 500,
                }}>
                <Fade in = {open}>
                    <div className = {classes.paper}>
                        <Container component="main" maxWidth="xs">
                            <Grid 
                                container direction = 'column' 
                                justifyContent="flex-start"
                                alignItems="center">
                            <Typography component="h1" variant="h5">
                            Sign Up
                            </Typography>
                            <form  noValidate onSubmit = {handleSubmit}>
                                <TextField
                                    variant="outlined"
                                    margin="normal"
                                    required
                                    fullWidth
                                    id="email"
                                    label="Email Address"
                                    name="email"
                                    autoComplete="email"
                                    autoFocus
                                    value = {account.email}
                                    onChange = { (e) => handleChange(e)}
                                />
                                <TextField
                                    variant="outlined"
                                    margin="normal"
                                    required
                                    fullWidth
                                    id="username"
                                    label="Username"
                                    name="username"
                                    autoComplete="username"
                                    autoFocus
                                    value = {account.username}
                                    onChange = { (e) => handleChange(e)}
                                />
                                <TextField
                                    variant="outlined"
                                    margin="normal"
                                    required
                                    fullWidth
                                    name="password"
                                    label="Password"
                                    type="password"
                                    id="password"
                                    autoComplete="current-password"
                                    value = {account.password}
                                    onChange = { (e) => handleChange(e)}
                                />
                                <Button
                                    type="submit"
                                    fullWidth
                                    variant="contained"
                                    color="primary"
                                >
                                    Sign Up
                                </Button>
                            </form>
                            </Grid>
                        </Container>
                    </div>
                </Fade>
            </Modal>
            {/* <form onSubmit = {handleSubmit}>
                <label>
                    Email:<br/>
                    <input type="text" value={account.email} onChange= {(e) => handleChange(e)} name = "email"/><br/>
                </label>
                <label>
                    Username:<br/>
                    <input type="text" value={account.username} onChange={(e) => handleChange(e)} name = "username"/><br/>
                </label>
                <label>
                    Password:<br/>
                    <input type="text" value={account.password} onChange={(e) => handleChange(e)} name = "password"/><br/>
                </label>
                <input type = "submit" value = "Submit"></input>
            </form> */}
        </div>
    );
}


export default Registration;
