import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import AxiosInstance from "../Axios";
import {Button, Modal, Fade, Backdrop, makeStyles} from "@material-ui/core";



const useStyles = makeStyles((theme) => ({
    modal: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    },
    paper: {
      backgroundColor: theme.palette.background.paper,
      border: '2px solid #000',
      boxShadow: theme.shadows[5],
      padding: theme.spacing(2, 4, 3),
    },
  }));


function Registration(prop){
    const classes  = useStyles();
    const[open, setOpen] = useState(false);
    const [account, setAccount] = useState({username: '', email: '', password: ''});
    let history = useHistory();
    function handleChange(e){
        const {name, value} = e.target;
        setAccount(prevState => ({
            ...prevState,
            [name]: value
         }));
    }


    function handleSubmit(e){
        e.preventDefault();
        AxiosInstance
            .post("/api/register/", account)
            .then(res =>{
                history.push("/login");
                console.log(res);
            })
            .catch((error) => {
                console.log(error.response);
            });
    }

    function handleClick(){
        setOpen(!open);
    }

    return(
        <div>
            <Button onClick = {handleClick}>{prop.text}</Button>
            <Modal
                open = {open}
                onClose = {handleClick}
                aria-labelledby="simple-modal-title"
                aria-describedby="simple-modal-description"
                className = {classes.modal}
                BackdropComponent = {Backdrop}
                BackdropProps = {{
                    timeout: 100,
                }}>
                <Fade in = {open}>
                    <div className = {classes.paper}>
                        This is a Modal
                    </div>
                </Fade>
            </Modal>
            {/* <form onSubmit = {handleSubmit}>
                <label>
                    Email:<br/>
                    <input type="text" value={account.email} onChange= {(e) => handleChange(e)} name = "email"/><br/>
                </label>
                <label>
                    Username:<br/>
                    <input type="text" value={account.username} onChange={(e) => handleChange(e)} name = "username"/><br/>
                </label>
                <label>
                    Password:<br/>
                    <input type="text" value={account.password} onChange={(e) => handleChange(e)} name = "password"/><br/>
                </label>
                <input type = "submit" value = "Submit"></input>
            </form> */}
        </div>
    );
}


export default Registration;
