import { React, useContext, useState} from "react";
import "./NavButton.css";
import Button from "./Button";
import { Modal, Backdrop, makeStyles, TextField} from "@material-ui/core";
import { UserContext } from "../App";
import { useHistory } from "react-router";

const useStyles = makeStyles((theme) => ({
    modal: {
        display: 'flex',
        alignItems: 'top',
        justifyContent: 'center',
    },
    paper: {
        borderRadius: '40px',
        display: 'flex',
        backgroundColor: 'white',
        margin: theme.spacing(6,5,40,5),
        padding: theme.spacing(3),
    }
  }));

function PostModal(){
    const history = useHistory();
    const user = useContext(UserContext);
    const classes = useStyles();
    const [open, setOpen] = useState(false);

    function handleClick(){
        if(user["username"] === 'noUser!')
            history.push("/signup");
        else if(user["username"])
            setOpen(!open);
    }

    return(
        <div>
            <Button onClick = {handleClick} width = "170px" text = "Post"/>
            <Modal
                open = {open}
                onClose = {() => {setOpen(!open);}}
                aria-labelledby="simple-modal-title"
                aria-describedby="simple-modal-description"
                className = {classes.modal}
                BackdropComponent = {Backdrop}
                BackdropProps = {{
                    timeout: 500,
                }}>
                <div className = {classes.paper}>
                <Container component="main" maxWidth="xs" style = {{paddingTop: "10%"}}>
            <div>
                <Typography component="h1" variant="h5">
                Sign Up
                </Typography>
                <form  className = {classes.form} noValidate onSubmit = {handleSubmit}>
                    <TextField
                        variant="outlined"
                        margin="normal"
                        required
                        fullWidth
                        id="email"
                        label="Email Address"
                        name="email"
                        autoComplete="email"
                        autoFocus
                        value = {account.email}
                        onChange = { (e) => handleChange(e)}
                    />
                    <TextField
                        variant="outlined"
                        margin="normal"
                        required
                        fullWidth
                        id="username"
                        label="Username"
                        name="username"
                        autoComplete="username"
                        autoFocus
                        value = {account.username}
                        onChange = { (e) => handleChange(e)}
                    />
                    <TextField
                        variant="outlined"
                        margin="normal"
                        required
                        fullWidth
                        name="password"
                        label="Password"
                        type="password"
                        id="password"
                        autoComplete="current-password"
                        value = {account.password}
                        onChange = { (e) => handleChange(e)}
                    />
                </form>
            </div>
        </Container>
                </div>
            </Modal>
        </div>
    );
}

export default PostModal;
