import { React, useContext, useState} from "react";
import "./NavButton.css";
import Button from "./Button";
import { Modal, Backdrop, makeStyles, TextField, Container, Grid} from "@material-ui/core";
import { UserContext } from "../App";
import { useHistory } from "react-router";

const useStyles = makeStyles((theme) => ({
    modal: {
        display: 'flex',
        alignItems: 'top',
        justifyContent: 'center',
    },
    paper: {
        borderRadius: '40px',
        display: 'flex',
        backgroundColor: 'white',
        margin: theme.spacing(6,5,13,5),
        padding: theme.spacing(6),
    },
    form: {
        width: '100%', // Fix IE 11 issue.
        marginTop: theme.spacing(1),
    },
  }));

function PostModal(){
    const history = useHistory();
    const user = useContext(UserContext);
    const classes = useStyles();
    const [open, setOpen] = useState(false);
    const [text, setText] = useState("");
    function handleClick(){
        if(user["username"] === 'noUser!')
            history.push("/signup");
        else if(user["username"])
            setOpen(!open);
    }

    function handleChange(e){
        setText(e.value);
    }
    function handleSubmit(){

    }

    return(
        <div>
            <Button onClick = {handleClick} width = "170px" text = "Post"/>
            <Modal
                open = {open}
                onClose = {() => {setOpen(!open);}}
                aria-labelledby="simple-modal-title"
                aria-describedby="simple-modal-description"
                className = {classes.modal}
                BackdropComponent = {Backdrop}
                BackdropProps = {{
                    timeout: 500,
                }}>
                <div className = {classes.paper}>
                    <Container component="main" maxWidth="xs">
                    <h1>Post</h1>
                    <form className={classes.form} onSubmit = {handleSubmit}>
                        <TextField
                            margin="normal"
                            required
                            fullWidth
                            id="email"
                            label="Title"
                            name="title"
                            autoFocus
                        />
                        <TextField
                            variant="outlined"
                            margin="normal"
                            required
                            fullWidth
                            maxRows = {12}
                            multiline
                            rows = {3}
                            name="content"
                            label="Content"
                            id="content"
                        />
                        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                        <Button text = "Submit" type = "submit" width = "140px" />
                    </form>
                        {/* <div>
                        <h1>Post</h1>
                        <form className = {classes.form}>
                            <TextField
                                margin="normal"
                                multiline
                                rows = {3}
                                required
                                fullWidth
                                value = {text}
                                autoFocus
                                onChange = { (e) => handleChange(e)}
                            />
                        </form>
                        <Button
                            type = "submit"
                            width = "120px"
                            text = "Submit"/>
                        </div> */}
                    </Container>
                </div>
            </Modal>
        </div>
    );
}

export default PostModal;
