import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import AxiosInstance from "../Axios";
import {Button, Modal, Fade, Backdrop, makeStyles, TextField,
 Grid,  Typography
    , Container} from "@material-ui/core";
import Signup2 from "./Signup2";
import Login2 from "./Login2";



const useStyles = makeStyles((theme) => ({
    modal: {
        display: 'flex',
        alignItems: 'top',
        justifyContent: 'center',
    },
    paper: {
        borderRadius: '40px',
        display: 'flex',
        backgroundColor: 'white',
        margin: theme.spacing(6,5,13,5),
        padding: theme.spacing(6),
    },
  }));



function LoginModal(props){
    const classes  = useStyles();
    const[open, setOpen] = useState(false);
    const[status, setStatus] = useState({...props.initial});

    function content(){
        if (status === "Login")
            return <Login2 setStatus = {setStatus}/>;
        if (status === "Signup")
            return <Signup2 setStatus = {setStatus}/>;
    }
   
    let history = useHistory();
    function handleClick(){
        setOpen(!open);
    }


    return(
        <div>
            <Button onClick = {handleClick}>{props.text}</Button>
            <Modal
                open = {open}
                onClose = {handleClick}
                aria-labelledby="simple-modal-title"
                aria-describedby="simple-modal-description"
                className = {classes.modal}
                BackdropComponent = {Backdrop}
                BackdropProps = {{
                    timeout: 500,
                }}>
                <Fade in = {open}>
                    <div className = {classes.paper}>
                        {content()}
                    </div>
                </Fade>
            </Modal>
            {/* <form onSubmit = {handleSubmit}>
                <label>
                    Email:<br/>
                    <input type="text" value={account.email} onChange= {(e) => handleChange(e)} name = "email"/><br/>
                </label>
                <label>
                    Username:<br/>
                    <input type="text" value={account.username} onChange={(e) => handleChange(e)} name = "username"/><br/>
                </label>
                <label>
                    Password:<br/>
                    <input type="text" value={account.password} onChange={(e) => handleChange(e)} name = "password"/><br/>
                </label>
                <input type = "submit" value = "Submit"></input>
            </form> */}
        </div>
    );
}


export default LoginModal;
