import { Grid } from "@material-ui/core";
import { React, useContext, useEffect, useRef, useState, } from "react";
import AxiosInstance from "../Axios";
import { UserContext } from "../App";

export default function Post(props){
    const post = props.post;
    const [vote,setVote] = useState(0);
    const [isLoading, setIsLoading] = useState(true);
    const user = useContext(UserContext);
    useEffect(()=>{
        AxiosInstance
            .get("/api/post/"+post.id+"/vote/")
            .then((res)=>{
                console.log(res);
                setIsLoading(false);
                setVote(res["data"]["vote"]);
            })
            .catch((error)=>{
                console.log(error);
            })
    },[vote]);

    function upvote(){
        AxiosInstance
            .post("/api/vote/", {post:post.id, user: user.id, comment: null})
            .then((res)=>{
                setVote(vote+1);
                console.log(res);
            })
            .catch((error)=>{
                console.log({post:post.id, user: user.id});
                console.log(error);
            })
    }

    function content(){
        return(
            <div>
                <a style = {{fontSize:"40px", fontWeight:"bold", }}>{post.title}</a>
                <p style = {{fontSize: "20px", }}>{post.description}</p>
                <p style = {{fontSize: "20px"}}>Vote: {vote}</p>
                <button onClick = {upvote}>Upvote</button>
                <br/>
            </div>
        );
    }

    return(
        content
    );
}
