import { React } from "react";
import {makeStyles, Grid,} from "@material-ui/core";
import NavButton from "./NavButton";
import { Navitems } from "./NavItems";
import LoginModal from "./LoginModal";
import Logout from './Logout';
import { UserContext } from "../App";
import { useContext } from "react";
import PostModal from "./PostModal";
const useStyles = makeStyles((theme)=>({
    root:{
        padding: theme.spacing(6,6,6,0),
        position: "fixed",
    },
    account: {
    }
}));

function ConditionalButton(){
    const user = useContext(UserContext);
    if(user["username"] === "noUser!") 
        return <LoginModal text = "Log in" initial = "Login"/>;
    else if(user["username"])
        return (
            <Grid container item direction = "column" >
                <PostModal/>
                <Logout/>
            </Grid>
        );
}

function Nav(props){
    const classes = useStyles();
    return(
        <Grid container item xs = {3} spacing = {1} justifyContent = "space-between" direction = "column">
            <Grid container item  direction = "column" spacing = {1} xs = {6}>
                {Navitems.map((item,index)=>{
                    const active = item.id === props.id ? true : false;
                    return (
                        <Grid item key = {item.id}>
                            <NavButton text = {item.title} path = {item.path} active = {active}>
                                {item.icon}
                            </NavButton>
                        </Grid> 
                    );
                })}
                
            </Grid>
            {ConditionalButton()}
        </Grid>
    );
}

export default Nav;
