import { Grid } from "@material-ui/core";
import { React, useContext, useEffect, useRef, useState, } from "react";
import AxiosInstance from "../Axios";
import { UserContext } from "../App";

export default function Post(props){
    const post = props.post;
    const [vote,setVote] = useState(0);
    const user = useContext(UserContext);
    useEffect(()=>{
        AxiosInstance
            .get("/api/post/"+post.id+"/vote/")
            .then((res)=>{
                console.log(res);
                setVote(res["data"]["vote"]);
            })
            .catch((error)=>{
                console.log(error);
            })
    },[vote]);

    function upvote(){
        AxiosInstance
            .post("/api/vote/", {post:post.id, user: user.id})
            .then((res)=>{
                setVote(vote+1);
                console.log(res);
            })
            .catch((error)=>{
                console.log(error);
            })
    }

    return(
        <Grid container direction = "column" style = {{marginTop: "50px"}}>
            <a style = {{whiteSpace:"nowrap", fontSize:"40px", fontWeight:"bold"}}>{post.title}</a>
            <p style = {{fontSize: "20px"}}>{post.description}</p>
            <p style = {{fontSize: "20px"}}>Vote: {vote}</p>
            <button onClick = {upvote}>Upvote</button>
            <br/>
        </Grid>
    )
}
