import { React } from "react";
import {makeStyles, Tabs, Tab, Grid,} from "@material-ui/core";
import NavButton from "./NavButton";
import { Navitems } from "./NavItems";
import LoginModal from "./LoginModal";

const useStyles = makeStyles((theme)=>({
    root:{
        padding: theme.spacing(6,6,6,13),
        position: "fixed",
    },
    account: {
        position: "fixed",
        bottom: theme.spacing(3),
    }
}));

function Nav(props){
    const classes = useStyles();
    return(
        <Grid container item xs = {3}>
            <div className = {classes.root}>
                <Grid container item  direction = "column" spacing = {1}>
                    {Navitems.map((item,index)=>{
                        const active = item.id === props.id ? true : false;
                        return (
                            <Grid item >
                                <NavButton text = {item.title} path = {item.path} active = {active}>
                                    {item.icon}
                                </NavButton>
                            </Grid> 
                        );
                    })}
                    
                </Grid>
                <div className = {classes.account}>
                    <LoginModal text = "login" initial = "Login"/>
                </div>
            </div>
        </Grid>
    );
}

export default Nav;
