import { Grid } from "@material-ui/core";
import { React, useContext, useEffect, useRef, useState, } from "react";
import AxiosInstance from "../Axios";
import { UserContext } from "../App";

export default function Post(props){
    const post = props.post;
    const [vote,setVote] = useState(0);
    const [isLoading, setIsLoading] = useState(true);
    const [isVoted, setIsVoted] = useState(false);
    const user = useContext(UserContext);
    useEffect(()=>{
        AxiosInstance
            .get("/api/post/"+post.id+"/vote/")
            .then((res)=>{
                console.log(res);
                setVote(res["data"]["vote"]);
                setIsLoading(false);
            })
            .catch((error)=>{
                console.log(error);
            })
        
        AxiosInstance
            .get("/api/vote/?post="+post.id+"&user="+user.id+"&comment=0")
            .then((res)=>{
                if(res["count"] === 0)
                    setIsVoted(true);
            })
            .catch((error)=>{
                console.log(error);
            })   
    },[vote]);

    function upvote(){
        AxiosInstance
            .post("/api/vote/", {post:post.id, user: user.id, comment: null})
            .then((res)=>{
                setVote(vote+1);
                console.log(res);
            })
            .catch((error)=>{
                console.log({post:post.id, user: user.id});
                console.log(error);
            })
    }

    function downvote(){
        AxiosInstance
            .delete("/api/vote/", {post:post.id, user: user.id, comment: null})
            .then((res)=>{
                setVote(vote-1);
                console.log(res);
            })
            .catch((error)=>{
                console.log(error);
            })
    }

    function renderVote(){
        if(isVoted)
            return downvote;
        return upvote;
    }

    if(isLoading)
        return null;

    return(
        <div>
            <a style = {{fontSize:"40px", fontWeight:"bold", }}>{post.title}</a>
            <p style = {{fontSize: "20px", }}>{post.description}</p>
            <p style = {{fontSize: "20px"}}>Vote: {vote}</p>
            <button onClick = {renderVote()}>Upvote</button>
            <br/>
        </div>
    )
}
